import React from "react";

class EditBurgerForm extends React.Component {
    render () {
        return (
            <div className="burger-edit">
                <input name= 'name'  onChange={this.handleChange}  type="text"  value={this.props.burger.name} />
                <input name= 'price'  onChange={this.handleChange} type="text"  value={this.props.burger.price} />
                <select className='status' onChange={this.handleChange} name='status' value={this.props.burger.status}>
                    <option value='available'>Доступно</option>
                    <option value='unavailable'>Не доступно</option>
                </select> 
                <textarea name= 'desc' onChange={this.handleChange}   type="text"  value={this.props.burger.desc} /> 
                <input name= 'image'  onChange={this.handleChange} type="text"  value={this.props.burger.image} />
            </div>
        )
    }
}

export default EditBurgerForm;
